sp_widget.client_script:
function($scope, $sce, spUtil) {
	/* widget controller */
	var c = this;

	var searchnow = document.getElementById("botssearch");
	searchnow.addEventListener("keydown", function (e) {
		if (e.keyCode === 13) {  //checks whether the pressed key is "Enter"
			c.searchbots();
		}
	});

	c.sort = function (newsort) {
		//number|DESC
		c.data.sort=newsort;
		var sortparts = newsort.split('|');
		c.data.sortfield = sortparts[0];
		c.data.sortdirection = sortparts[1];
		c.server.update();
	};

	c.searchbots = function () {
		c.data.botskeywords = c.data.botssearchterm;
		c.server.update();
	};

	c.resetbots = function () {
		c.data.botskeywords = '';
		c.data.botssearchterm = '';
		c.server.update();
	};

	c.filterbots = function (catalog) {
		c.data.onform = catalog;
		c.server.update();
	};

	c.fiscialyear = function (year) {
		c.data.foryear = year;
		c.server.update();
	};

	$scope.highlight = function(haystack, needle) {
		if (!haystack)
			return "";

		if (!needle)
			return $sce.trustAsHtml(haystack);

		return $sce.trustAsHtml(haystack.replace(new RegExp(needle, "gi"), function(match) {
			return '<mark class="highlight">' + match + '</mark>';
		}));
	};

}

