sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	var findstate = new GlideRecord('x_63474_bots_bots_requests');
	findstate.get($sp.getParameter("sys_id"));

	data.isrequest = 'false';
  if (findstate.getRowCount() != 0)
		{
			data.isrequest = 'true';
		}
	if (findstate.state == '1')//Initial aka step1
		{
		data.classone = 'active';
		data.classtwo = 'disabled';
		data.classthree = 'disabled';
		data.classfour = 'disabled';
		data.classfive = 'disabled';

		data.stateone = 'current';
		data.statetwo = 'future';
		data.statethree = 'future';
		data.statefour = 'future';
		data.statefive = 'future';

		data.currentone = 'step';
		data.currenttwo = 'false';
		data.currentthree = 'false';
		data.currentfour = 'false';
		data.currentfive = 'false';
		data.selectedone = 'true';
		data.selectedtwo = 'false';
		data.selectedthree = 'false';
		data.selectedfour = 'false';
		data.selectedfive = 'false';

	}
	else if (findstate.state == '2')//Approve aka step2
		{
		data.classone = 'disabled';
		data.classtwo = 'active';
		data.classthree = 'disabled';
		data.classfour = 'disabled';
		data.classfive = 'disabled';

		data.stateone = 'past';
		data.statetwo = 'current';
		data.statethree = 'future';
		data.statefour = 'future';
		data.statefive = 'future';

		data.currentone = 'false';
		data.currenttwo = 'step';
		data.currentthree = 'false';
		data.currentfour = 'false';
		data.currentfive = 'false';
		data.selectedone = 'false';
		data.selectedtwo = 'true';
		data.selectedthree = 'false';
		data.selectedfour = 'false';
		data.selectedfive = 'false';

	}
	else if (findstate.state == '5' || findstate.state == '6')//Review BM  or Review LD aka step3
		{
		data.classone = 'disabled';
		data.classtwo = 'disabled';
		data.classthree = 'active';
		data.classfour = 'disabled';
		data.classfive = 'disabled';

		data.stateone = 'past';
		data.statetwo = 'past';
		data.statethree = 'current';
		data.statefour = 'future';
		data.statefive = 'future';

		data.currentone = 'false';
		data.currenttwo = 'false';
		data.currentthree = 'step';
		data.currentfour = 'false';
		data.currentfive = 'false';
		data.selectedone = 'false';
		data.selectedtwo = 'false';
		data.selectedthree = 'true';
		data.selectedfour = 'false';
		data.selectedfive = 'false';
	}
	else if (findstate.state == '4')//Closed aka step4
		{
		data.classone = 'disabled';
		data.classtwo = 'disabled';
		data.classthree = 'disabled';
		data.classfour = 'active';
		data.classfive = 'disabled';

		data.stateone = 'past';
		data.statetwo = 'past';
		data.statethree = 'past';
		data.statefour = 'current';
		data.statefive = 'past';

		data.currentone = 'false';
		data.currenttwo = 'false';
		data.currentthree = 'false';
		data.currentfour = 'step';
		data.currentfive = 'false';
		data.selectedone = 'false';
		data.selectedtwo = 'false';
		data.selectedthree = 'false';
		data.selectedfour = 'true';
		data.selectedfive = 'false';

	}
	else if (findstate.state == '3')//Canceled aka step5
		{
		data.classone = 'disabled';
		data.classtwo = 'disabled';
		data.classthree = 'disabled';
		data.classfour = 'disabled';
		data.classfive = 'active';

		data.stateone = 'past';
		data.statetwo = 'past';
		data.statethree = 'past';
		data.statefour = 'past';
		data.statefive = 'current';

		data.currentone = 'false';
		data.currenttwo = 'false';
		data.currentthree = 'false';
		data.currentfour = 'false';
		data.currentfive = 'step';
		data.selectedone = 'false';
		data.selectedtwo = 'false';
		data.selectedthree = 'false';
		data.selectedfour = 'false';
		data.selectedfive = 'true';

	}


})();
