var answer = [];
var owner ;
var budgetgroup = 'budget-leadership-'+ current.funding_source.u_budget_short_name;
var usercount = 1;
var ownergrouptest = new GlideRecord('sys_user_group');
ownergrouptest.addQuery('u_sims_name',budgetgroup);
ownergrouptest.query();
if(ownergrouptest.next()) {
	
	//// Set appproved approvals to another state
	
	var appGroup = new GlideRecord('sysapproval_group');
	appGroup.addQuery('assignment_group',ownergrouptest.sys_id);
	appGroup.addQuery('parent',current.sys_id);
	appGroup.query();
	if(appGroup.next()){
		var app = new GlideRecord('sysapproval_approver');
		app.addQuery('group',appGroup.sys_id);
		app.addQuery('state','approved');
		app.query();
		while(app.next()){
			app.state = 'requested';
			app.comments = "approval is set to Requested again as second set of approval requested";
			app.update();
		}
	}
	
	/////////////////////////////////////////
	
	
	current.assignment_group = ownergrouptest.sys_id;
	owner = ownergrouptest.sys_id;
	//answer.push(ownergrouptest.sys_id);
	var hasmembers = new GlideRecord('sys_user_grmember');
	hasmembers.addQuery('group',ownergrouptest.sys_id);
	hasmembers.query();
	while(hasmembers.next()) {
		if (usercount==1)
			{
			current.assigned_to = hasmembers.user;
		}
		else
			{
			current.additional_assignee_list = current.additional_assignee_list + hasmembers.user+",";
		}
		usercount = usercount+1;
	}
	
}
answer.push(owner);

