var answer = [];
var budgetgroup = 'BOTS-Leadership-biz';   //'budget-owner-'+ current.funding_source.u_budget_short_name;
var usercount = 1;
var ownergrouptest = new GlideRecord('sys_user_group');
ownergrouptest.addQuery('u_sims_name',budgetgroup).addOrCondition('sys_id','b5b3d7fd6f25db8051281ecbbb3ee40a');
ownergrouptest.query();
while(ownergrouptest.next()) {
	var member = new GlideRecord('sys_user_grmember');
	member.addQuery('group',ownergrouptest.sys_id);
	//member.addQuery('user',current.funding_source.u_budget_owner);
	member.query();
	if(member.next()){
		//if(current.funding_source.u_budget_owner != current.requested_by){
			current.assignment_group = ownergrouptest.sys_id;
			current.u_substate = 'business_leadership_approval';
			//answer.push(ownergrouptest.sys_id);
			var hasmembers = new GlideRecord('sys_user_grmember');
			hasmembers.addQuery('group',ownergrouptest.sys_id);
			hasmembers.orderByDesc('sys_created_on');
			hasmembers.query();
			while(hasmembers.next()) {
				if (usercount==1)
					{
					current.assigned_to = hasmembers.user;
				}
				else
					{
					current.additional_assignee_list = current.additional_assignee_list + hasmembers.user+",";
				}
				usercount = usercount+1;
			}
	//	}
	}
}

