}

answer = ifScript();

function ifScript() {
	if (workflow.scratchpad.task_close_state == 3) {
		return 'yes';
	}
	return 'no';
}

workflow.scratchpad.groupsgood = 'false';
workflow.scratchpad.groupsfound = 'false';
workflow.scratchpad.hasmembers = 'false';
var budgetgroup = 'budget-owner-'+ current.funding_source.u_budget_short_name;

var ownergrouptest = new GlideRecord('sys_user_group');
ownergrouptest.addQuery('u_sims_name',budgetgroup);
ownergrouptest.query();
while(ownergrouptest.next()) {
	workflow.scratchpad.groupsfound = 'true';
	var hasmembers = new GlideRecord('sys_user_grmember');
	hasmembers.addQuery('group',ownergrouptest.sys_id);
	hasmembers.query();
	if (hasmembers.getRowCount() > 0)
		{
			workflow.scratchpad.hasmembers = 'true';
			workflow.scratchpad.groupsgood = 'true';
		}
}

workflow.scratchpad.groupsgood = 'false';
workflow.scratchpad.groupsfound = 'false';
workflow.scratchpad.hasmembers = 'false';
var budgetgroup = 'budget-owner-'+ current.funding_source.u_budget_short_name;

var ownergrouptest = new GlideRecord('sys_user_group');
ownergrouptest.addQuery('u_sims_name',budgetgroup);
ownergrouptest.query();
while(ownergrouptest.next()) {
	workflow.scratchpad.groupsfound = 'true';
	var hasmembers = new GlideRecord('sys_user_grmember');
	hasmembers.addQuery('group',ownergrouptest.sys_id);
	hasmembers.query();
	if (hasmembers.getRowCount() > 0)
		{
			workflow.scratchpad.hasmembers = 'true';
			workflow.scratchpad.groupsgood = 'true';
		}
}
